<?phpsession_start();// Création d'un nouvel objet Langue avec comme paramètres le dossier langue et le fichier xml à charger (et en facultatif une session contenant la langue à charger, peut être utilisé dans un système de membre, avec une session contenant la langue du membre).if (isset($_SESSION['langue'])){	$langue = new Langues('langues', $page, $_SESSION['langue']);}else{	$langue = new Langues('langues', $page);	$tmplg = strtolower(substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2));	if ($tmplg <> 'fr' and $tmplg <> 'de' and $tmplg <> 'en')	{		$_SESSION['langue'] = 'en';	}	else	{		$_SESSION['langue'] = strtolower(substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2));	}}?>